function findBinary(decimal) {
  var base      = 2;
  var index     = 0;
  var reference = parseInt(decimal, 10);
  var remainder = reference;
  var output    = [];

  if (reference !== reference || reference < 0) {
    return 'You should pass a number equals or greater than 0';
  } else if (reference === 0) {
    return '0';
  }

  while (reference >= Math.pow(base, index)) {
    index += 1;
  }

  while (remainder >= 0 && index > 0) {
    if (remainder - Math.pow(base, index - 1) >= 0) {
      output.push('1');
      remainder -= Math.pow(base, index - 1);
    } else {
      output.push('0');
    }

    index -= 1;
  }

  return output.join('');
}

module.exports = findBinary;
